import React, { Component } from 'react';

class RestorantCreate extends Component {
    constructor(){
        super();
        this.state = {
            name:null,
            email:null,
            address:null,
            rating:null,
        }

    }
    create(){
        fetch('http://localhost:3000/resto/',{
            method:'Post',
            body:JSON.stringify(this.state),
            headers:{'Content-Type':'application/json'}
        }).then(
            (response)=>{response.json().then(
                (result)=>{console.warn(result)})})
    }
    render() {
        return (
            <div>
                <h2>Restorant Create Component</h2>
                <input 
                name="name" 
                onChange={(event)=>this.setState({name:event.target.value})} 
                placeholder="Restorant Name" /><br /><br />
                <input 
                name="email" 
                onChange={(event)=>this.setState({email:event.target.value})} 
                placeholder="Restorant Email" /><br /><br />
                <input 
                name="address" 
                onChange={(event)=>this.setState({address:event.target.value})} 
                placeholder="Restorant Address" /><br /><br />
                <input 
                name="rating" 
                onChange={(event)=>this.setState({rating:event.target.value})} 
                placeholder="Restorant Rating" /><br /><br />
                <button onClick={()=>this.create()} >Add Restorant</button>
            </div>
        );
    }
}

export default RestorantCreate;
