    }
    render() {
        return (
            <div>
                <CustomNavbar />
                <h2>Login Page</h2>
                <input
                    type="text"
                    name='username'
                    placeholder="Username"
                    onChange={(e) => this.setState({ username: e.target.value })}
                /><br /><br />
                <input
                    type="password"
                    name='password'
                    placeholder="Password"
                    onChange={(e) => this.setState({ password: e.target.value })}
                /><br /><br />
                <button onClick={() => this.login()}>Login</button>
            </div>
        );
    }
}

export default Login;
