import React, { Component } from 'react';

class RestorantList extends Component {
    constructor() {
        super();
        this.state = {
            list: null
        }
    }
    componentDidMount() {
        fetch('http://localhost:3000/resto/').then((response) => {
            response.json().then((result) => {
                this.setState({
                    list: result,
                })
            })
        })
    }
    render() {
        return (
            <div>
                <h2>Restorant List Component</h2>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Address</th>
                            <th>Rating</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.list ?
                                this.state.list.map(
                                    (item, index) =>
                                    // <div className="list-data">
                                    //     <span>{index}</span>
                                    //     <span>{item.name}</span>
                                    //     <span>{item.email}</span>
                                    //     <span>{item.address}</span>
                                    //     <span>{item.rating}</span>
                                    // </div>

                                    <tr>
                                    <td>{index}</td>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.address}</td>
                                    <td>{item.rating}</td>
                                    </tr>

                                ) :
                                <div>There is no record!</div>
                        }
                    </tbody>
                </Table>
            </div>
        );
    }
}

export default RestorantList;
