import { LOGIN } from "../Constant";

export default function loginProcess(state=[],action){
    switch(action.type){
        case LOGIN:
            console.log("Reducer",action.data)
            return [
                ...state,
                {loginData:action.data}
            ]
            break;
        default:
            return state
    }
}
