import React, { Component } from 'react';

class Login extends Component {
    constructor(){
        super()
        this.state = {
            username:"",
            password:"",
        }
    }
    login() {
        console.log(this.state)
        fetch('http://localhost:3000/login?q=' + this.state).then(
            (response) => {
                response.json().then(
                    (result) => {
                        console.warn(result)
                        result.length > 0 ? console.log(this.props) : alert("Invalid username or password");
                    })
            })
    }
    render() {
        return (
            <div>
                <h2>Login Page</h2>
                <input 
                type="text" 
                name='username' 
                placeholder="Username"
                onChange={(e)=>this.setState({username:e.target.value})} 
                /><br /><br />
                <input 
                type="password" 
                name='password'
                placeholder="Password" 
                onChange={(e)=>this.setState({password:e.target.value})} 
                /><br /><br />
                <button onClick={()=>this.login()}>Login</button>
            </div>
        );
    }
}

export default Login;
