import { faEdit } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { Component } from 'react';
import { Table, tbody, thead, th, tr, td } from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';


class RestorantList extends Component {
    constructor() {
        super();
        this.state = {
            list: null
        }
    }
    componentDidMount() {
        fetch('http://localhost:3000/resto/').then((response) => {
            response.json().then((result) => {
                this.setState({
                    list: result,
                })
            })
        })
    }
    render() {
        return (
            <div>
                <h2>Restorant List Component</h2>
                {
                    this.state.list ?
                        <div>
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Name</th>
                                        <th>Email</th>
                                        <th>Address</th>
                                        <th>Rating</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>{
                                    this.state.list.map(
                                        (item, index) =>
                                            <tr key={item.id}>
                                                <td>{index}</td>
                                                <td>{item.name}</td>
                                                <td>{item.email}</td>
                                                <td>{item.address}</td>
                                                <td>{item.rating}</td>
                                                <td><FontAwesomeIcon icon={faEdit} /> <Link to={"/update/"+item.id}>Edit</Link></td>
                                            </tr>

                                    )}</tbody></Table></div> :
                        <p>There is no record!</p>
                }
            </div>
        );
    }
}

export default RestorantList;
