import React, { Component } from 'react';
import { Table, tbody, thead, th, tr, td } from 'react-bootstrap';


class RestorantList extends Component {
    constructor() {
        super();
        this.state = {
            list: null
        }
    }
    componentDidMount() {
        fetch('http://localhost:3000/resto/').then((response) => {
            response.json().then((result) => {
                this.setState({
                    list: result,
                })
            })
        })
    }
    render() {
        return (
            <div>
                <h2>Restorant List Component</h2>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Address</th>
                            <th>Rating</th>
                        </tr>
                    </thead>
                        {
                            this.state.list ?<tbody>{
                                this.state.list.map(
                                    (item, index) =>
                                        <tr>
                                            <td>{index}</td>
                                            <td>{item.name}</td>
                                            <td>{item.email}</td>
                                            <td>{item.address}</td>
                                            <td>{item.rating}</td>
                                        </tr>

                                )} </tbody>:
                                <div>There is no record!</div>
                        }
                </Table>
            </div>
        );
    }
}

export default RestorantList;
